package notableprimes

import (
	"testing"

	"github.com/sourcekris/goRsaTool/keys"
	"github.com/sourcekris/goRsaTool/ln"
	"github.com/sourcekris/goRsaTool/utils"

	fmp "github.com/sourcekris/goflint"
)

func TestLucasNumber(t *testing.T) {
	tt := []struct {
		name string
		n    int
		want *fmp.Fmpz
	}{
		{
			name: "lucas 113 (which is prime btw)",
			n:    113,
			want: ln.FmpString("412670427844921037470771"),
		},
	}

	for _, tc := range tt {
		got := lucasNumber(tc.n)
		if !got.Equals(tc.want) {
			t.Errorf("lucasNumber() failed: %s - got %v wanted %v", tc.name, got, tc.want)
		}
	}
}

func TestAttack(t *testing.T) {
	tt := []struct {
		name string
		n    *fmp.Fmpz
		want *fmp.Fmpz
	}{
		{
			name: "vulnerable key with mersenne prime as a factor",
			n:    ln.FmpString(""),
			want: ln.FmpString(""),
		},
		{
			name: "vulnerable key with lucas prime as a factor",
			n:    ln.FmpString("113448173311413939839000623599657493093009426396217979162243983168541031494892451251690778765048955614538230433937083862629739306836776161799668560604812661395794495338674551258564955228997049521362823039716218760124616598902153917018832969382897531142500491"),
			want: ln.FmpString("128654053254067001789796154414677061029563911266334766544884715737078582075546200311143377241563666249432665857145030497939634521"),
		},
		{
			name: "vulnerable key with 31337 prime as a factor",
			n:    ln.FmpString(""),
			want: ln.FmpString("313333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333337"),
		},
		{
			name: "vulnerable key with 1337 prime as a factor",
			n:    ln.FmpString(""),
			want: ln.FmpString("13333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333333337"),
		},
	}

	for _, tc := range tt {
		fmpPubKey := &keys.FMPPublicKey{
			N: tc.n,
			E: fmp.NewFmpz(65537),
		}

		k, _ := keys.NewRSA(keys.PrivateFromPublic(fmpPubKey), nil, nil, "", false)
		err := Attack([]*keys.RSA{k})
		if err != nil {
			t.Errorf("Attack() failed: %s expected no error got error: %v", tc.name, err)
		}

		if !utils.FoundP(tc.want, k.Key.Primes) {
			t.Errorf("Attack() failed: %s expected primes not found - got %v wanted %v", tc.name, k.Key.Primes, tc.want)
		}
	}
}
