package libnum

import "math/big"
import "testing"

func TestFindGcd(t *testing.T) {
  a := big.NewInt(121891891892166)
  b := big.NewInt(15874264264299962)
  c := big.NewInt(1231231231234)
  d := new(big.Int).Set(FindGcd(a, b))

  if c.Cmp(d) != 0 {
    t.Errorf("got %d; want %d for a = %d and b = %d\n", d, c, a, b)
  }
}

func TestNthRoot(t *testing.T) {
  a, _ := new(big.Int).SetString("",10)
  b, _ := new(big.Int).SetString("", 10)
  c    := int64(3)
  d    := NthRoot(c, a)

  if b.Cmp(d) != 0 {
    t.Errorf("got %d; want %d for a = %d and c = %d\n", d, b, a, c)
  }
}